# Example 1: Simple if-else
age = int(input("How old are you? "))

if age >= 18:
    print("You are an adult.")
else:
    print("You are a minor.")

# Example 2: if-elif-else with multiple conditions
score = int(input("Enter your score: "))

if score >= 90:
    print("Grade: A")
elif score >= 80:
    print("Grade: B")
elif score >= 70:
    print("Grade: C")
else:
    print("Grade: F")

# Example 4: Nested conditionals
age = int(input("How old are you? "))

if age >= 18:
    has_permission = input("Do you have permission? (yes/no): ").lower() == "yes"
    if has_permission:
        print("Access granted.")
    else:
        print("Access denied - no permission.")
else:
    print("Access denied - too young.")
